@import "tailwindcss";
@import "tw-animate-css";
@import url("https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap");

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  /* Entity colors */
  --color-entity-courses: var(--entity-courses);
  --color-entity-instructors: var(--entity-instructors);
  --color-entity-participants: var(--entity-participants);
  --color-entity-rooms: var(--entity-rooms);
  --color-entity-registrations: var(--entity-registrations);
  --color-surface: var(--surface);
}

:root {
  --radius: 0.75rem;
  --font-sans: "Plus Jakarta Sans", sans-serif;

  /* Warm cool slate background */
  --background: oklch(0.975 0.005 250);
  --foreground: oklch(0.165 0.03 255);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.165 0.03 255);
  --surface: oklch(1 0 0);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.165 0.03 255);

  /* Deep indigo primary */
  --primary: oklch(0.52 0.22 264);
  --primary-foreground: oklch(0.99 0 0);
  --primary-glow: oklch(0.65 0.18 264);

  --secondary: oklch(0.96 0.01 250);
  --secondary-foreground: oklch(0.165 0.03 255);
  --muted: oklch(0.96 0.008 250);
  --muted-foreground: oklch(0.52 0.02 255);
  --accent: oklch(0.94 0.015 264);
  --accent-foreground: oklch(0.52 0.22 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.008 250);
  --input: oklch(0.93 0.006 250);
  --ring: oklch(0.52 0.22 264 / 0.4);

  /* Entity accent colors */
  --entity-courses: oklch(0.52 0.22 264);     /* indigo */
  --entity-instructors: oklch(0.62 0.17 160); /* emerald */
  --entity-participants: oklch(0.72 0.18 65);  /* amber */
  --entity-rooms: oklch(0.55 0.2 290);         /* violet */
  --entity-registrations: oklch(0.58 0.2 15);  /* rose */

  /* Gradients */
  --gradient-primary: linear-gradient(135deg, oklch(0.52 0.22 264), oklch(0.6 0.2 280));
  --gradient-surface: linear-gradient(180deg, oklch(0.975 0.005 250), oklch(0.965 0.008 255));

  /* Shadows */
  --shadow-card: 0 1px 3px oklch(0.165 0.03 255 / 0.08), 0 4px 12px oklch(0.165 0.03 255 / 0.05);
  --shadow-elevated: 0 4px 20px oklch(0.165 0.03 255 / 0.12), 0 1px 4px oklch(0.165 0.03 255 / 0.06);
  --shadow-primary: 0 4px 20px oklch(0.52 0.22 264 / 0.3);

  /* Dark sidebar */
  --sidebar: oklch(0.17 0.03 255);
  --sidebar-foreground: oklch(0.72 0.02 255);
  --sidebar-primary: oklch(0.52 0.22 264);
  --sidebar-primary-foreground: oklch(0.99 0 0);
  --sidebar-accent: oklch(0.22 0.03 255);
  --sidebar-accent-foreground: oklch(0.92 0.01 255);
  --sidebar-border: oklch(0.25 0.025 255);
  --sidebar-ring: oklch(0.52 0.22 264);

  /* Charts */
  --chart-1: oklch(0.52 0.22 264);
  --chart-2: oklch(0.62 0.17 160);
  --chart-3: oklch(0.72 0.18 65);
  --chart-4: oklch(0.55 0.2 290);
  --chart-5: oklch(0.58 0.2 15);

  /* Transitions */
  --transition-smooth: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  --transition-spring: all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-sans);
    font-weight: 700;
    letter-spacing: -0.02em;
  }
}

@layer components {
  /* KPI Cards */
  .kpi-card {
    background: var(--card);
    border-radius: var(--radius);
    box-shadow: var(--shadow-card);
    border: 1px solid oklch(0.91 0.008 250);
    transition: var(--transition-smooth);
  }
  .kpi-card:hover {
    box-shadow: var(--shadow-elevated);
    transform: translateY(-1px);
  }

  /* Entity badges */
  .badge-courses {
    background: oklch(0.52 0.22 264 / 0.1);
    color: oklch(0.42 0.22 264);
    border: 1px solid oklch(0.52 0.22 264 / 0.2);
  }
  .badge-instructors {
    background: oklch(0.62 0.17 160 / 0.1);
    color: oklch(0.42 0.17 160);
    border: 1px solid oklch(0.62 0.17 160 / 0.2);
  }
  .badge-participants {
    background: oklch(0.72 0.18 65 / 0.15);
    color: oklch(0.48 0.18 65);
    border: 1px solid oklch(0.72 0.18 65 / 0.25);
  }
  .badge-rooms {
    background: oklch(0.55 0.2 290 / 0.1);
    color: oklch(0.42 0.2 290);
    border: 1px solid oklch(0.55 0.2 290 / 0.2);
  }
  .badge-registrations {
    background: oklch(0.58 0.2 15 / 0.1);
    color: oklch(0.42 0.2 15);
    border: 1px solid oklch(0.58 0.2 15 / 0.2);
  }
  .badge-paid {
    background: oklch(0.62 0.17 160 / 0.1);
    color: oklch(0.38 0.17 160);
    border: 1px solid oklch(0.62 0.17 160 / 0.25);
  }
  .badge-unpaid {
    background: oklch(0.58 0.2 15 / 0.1);
    color: oklch(0.42 0.2 15);
    border: 1px solid oklch(0.58 0.2 15 / 0.2);
  }

  /* Sidebar nav item */
  .nav-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.625rem 0.875rem;
    border-radius: 0.5rem;
    font-size: 0.875rem;
    font-weight: 500;
    color: oklch(0.65 0.02 255);
    transition: var(--transition-smooth);
    cursor: pointer;
    border: none;
    background: transparent;
    width: 100%;
    text-align: left;
  }
  .nav-item:hover {
    background: oklch(0.22 0.03 255);
    color: oklch(0.92 0.01 255);
  }
  .nav-item.active {
    background: oklch(0.52 0.22 264 / 0.2);
    color: oklch(0.78 0.16 264);
  }

  /* Table styles */
  .data-table-row {
    transition: background 0.15s ease;
  }
  .data-table-row:hover {
    background: oklch(0.52 0.22 264 / 0.03);
  }

  /* Primary action button */
  .btn-primary {
    background: var(--gradient-primary);
    color: oklch(0.99 0 0);
    box-shadow: var(--shadow-primary);
    border: none;
    font-weight: 600;
    letter-spacing: -0.01em;
    transition: var(--transition-smooth);
  }
  .btn-primary:hover {
    opacity: 0.9;
    box-shadow: 0 6px 24px oklch(0.52 0.22 264 / 0.4);
    transform: translateY(-1px);
  }

  /* Page header */
  .page-header {
    background: var(--card);
    border-bottom: 1px solid oklch(0.91 0.008 250);
  }

  /* Form dialog */
  .form-section {
    border-radius: 0.5rem;
    background: oklch(0.975 0.005 250);
    border: 1px solid oklch(0.91 0.008 250);
    padding: 1rem;
  }

  /* Loading skeleton */
  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }
  .skeleton {
    background: linear-gradient(
      90deg,
      oklch(0.93 0.005 250) 25%,
      oklch(0.96 0.003 250) 50%,
      oklch(0.93 0.005 250) 75%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
    border-radius: 0.375rem;
  }
}

