import { useState } from "react";
import { AppSidebar, type ActiveView } from "@/components/AppSidebar";
import { OverviewDashboard } from "@/components/OverviewDashboard";
import { CoursesPage, type Course } from "./CoursesPage";
import { InstructorsPage, type Instructor } from "./InstructorsPage";
import { ParticipantsPage, type Participant } from "./ParticipantsPage";
import { RoomsPage, type Room } from "./RoomsPage";
import { RegistrationsPage, type Registration } from "./RegistrationsPage";

// ── Mock data ──────────────────────────────────────────────────────────────
const mockCourses: Course[] = [
  { record_id: "c1", title: "Einführung in Python", description: "Grundlagen der Programmierung mit Python", start_date: "2026-03-01", end_date: "2026-03-31", max_participants: 20, price: 299 },
  { record_id: "c2", title: "React für Fortgeschrittene", description: "Hooks, State Management und Performance", start_date: "2026-04-01", end_date: "2026-04-30", max_participants: 15, price: 499 },
  { record_id: "c3", title: "Business English", description: "Englisch für den Berufsalltag", start_date: "2026-03-15", end_date: "2026-06-15", max_participants: 12, price: 399 },
];
const mockInstructors: Instructor[] = [
  { record_id: "i1", name: "Dr. Anna Müller", email: "a.mueller@example.de", phone: "+49 30 12345678", subject_area: "Informatik" },
  { record_id: "i2", name: "Thomas Schneider", email: "t.schneider@example.de", phone: "+49 89 98765432", subject_area: "Webentwicklung" },
  { record_id: "i3", name: "Sarah Johnson", email: "s.johnson@example.de", phone: "+49 40 55554444", subject_area: "Sprachen" },
];
const mockParticipants: Participant[] = [
  { record_id: "p1", name: "Max Muster", email: "max@example.de", phone: "+49 171 1234567", birthdate: "1992-06-15" },
  { record_id: "p2", name: "Lisa Hoffmann", email: "lisa@example.de", phone: "+49 172 9876543", birthdate: "1988-11-03" },
  { record_id: "p3", name: "Felix Wagner", email: "felix@example.de", phone: "+49 173 5556677", birthdate: "1995-02-28" },
  { record_id: "p4", name: "Julia Braun", email: "julia@example.de", phone: "+49 174 3334455", birthdate: "2000-07-20" },
];
const mockRooms: Room[] = [
  { record_id: "r1", room_name: "Seminarraum A1", building: "Hauptgebäude", capacity: 25 },
  { record_id: "r2", room_name: "Computerraum 3", building: "Technikgebäude", capacity: 20 },
  { record_id: "r3", room_name: "Schulungsraum B", building: "Nebengebäude", capacity: 15 },
];
const mockRegistrations: Registration[] = [
  { record_id: "reg1", participant_id: "p1", course_id: "c1", registration_date: "2026-02-01", paid: true },
  { record_id: "reg2", participant_id: "p2", course_id: "c1", registration_date: "2026-02-03", paid: true },
  { record_id: "reg3", participant_id: "p3", course_id: "c2", registration_date: "2026-02-10", paid: false },
  { record_id: "reg4", participant_id: "p4", course_id: "c3", registration_date: "2026-02-12", paid: false },
  { record_id: "reg5", participant_id: "p1", course_id: "c3", registration_date: "2026-02-14", paid: true },
];
// ──────────────────────────────────────────────────────────────────────────

export default function Dashboard() {
  const [activeView, setActiveView] = useState<ActiveView>("dashboard");
  const [courses, setCourses] = useState<Course[]>(mockCourses);
  const [instructors, setInstructors] = useState<Instructor[]>(mockInstructors);
  const [participants, setParticipants] = useState<Participant[]>(mockParticipants);
  const [rooms, setRooms] = useState<Room[]>(mockRooms);
  const [registrations, setRegistrations] = useState<Registration[]>(mockRegistrations);

  // ── Generic CRUD helpers ────────────────────────────────────────────────
  function makeId() {
    return Math.random().toString(36).slice(2);
  }

  // Courses CRUD
  const courseHandlers = {
    onCreate: async (data: Omit<Course, "record_id">) => {
      setCourses((prev) => [...prev, { ...data, record_id: makeId() }]);
    },
    onUpdate: async (id: string, data: Omit<Course, "record_id">) => {
      setCourses((prev) => prev.map((r) => (r.record_id === id ? { ...r, ...data } : r)));
    },
    onDelete: async (id: string) => {
      setCourses((prev) => prev.filter((r) => r.record_id !== id));
    },
  };

  // Instructors CRUD
  const instructorHandlers = {
    onCreate: async (data: Omit<Instructor, "record_id">) => {
