import { useState } from "react";
import { AppSidebar, type ActiveView } from "@/components/AppSidebar";
import { OverviewDashboard } from "@/components/OverviewDashboard";
import { CoursesPage, type Course } from "./CoursesPage";
import { InstructorsPage, type Instructor } from "./InstructorsPage";
import { ParticipantsPage, type Participant } from "./ParticipantsPage";
import { RoomsPage, type Room } from "./RoomsPage";
import { RegistrationsPage, type Registration } from "./RegistrationsPage";

// ── Mock data ──────────────────────────────────────────────────────────────
const mockCourses: Course[] = [
  { record_id: "c1", title: "Einführung in Python", description: "Grundlagen der Programmierung mit Python", start_date: "2026-03-01", end_date: "2026-03-31", max_participants: 20, price: 299 },
  { record_id: "c2", title: "React für Fortgeschrittene", description: "Hooks, State Management und Performance", start_date: "2026-04-01", end_date: "2026-04-30", max_participants: 15, price: 499 },
  { record_id: "c3", title: "Business English", description: "Englisch für den Berufsalltag", start_date: "2026-03-15", end_date: "2026-06-15", max_participants: 12, price: 399 },
];
const mockInstructors: Instructor[] = [
  { record_id: "i1", name: "Dr. Anna Müller", email: "a.mueller@example.de", phone: "+49 30 12345678", subject_area: "Informatik" },
  { record_id: "i2", name: "Thomas Schneider", email: "t.schneider@example.de", phone: "+49 89 98765432", subject_area: "Webentwicklung" },
